CREATE TABLE kondisiruangan(
   ID int NOT NULL AUTO_INCREMENT PRIMARY KEY
  ,Nama_Kecamatan VARCHAR(16) NOT NULL 
  ,Nama_Sekolah   VARCHAR(50) NOT NULL
  ,Baik           INTEGER  NOT NULL
  ,Rusak_Ringan   INTEGER  NOT NULL
  ,Rusak_Sedang   INTEGER  NOT NULL
  ,Rusak_Berat    INTEGER  NOT NULL
  ,Jumlah_Ruangan INTEGER  NOT NULL
);
